import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Label } from "@/components/ui/label";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertKpiMetricSchema, type InsertKpiMetric } from "@shared/schema";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

const METRIC_TYPES = [
  { value: "deployment_frequency", label: "Deployment Frequency", unit: "per week" },
  { value: "lead_time", label: "Lead Time for Changes", unit: "hours" },
  { value: "mttr", label: "Mean Time to Recovery", unit: "minutes" },
  { value: "failure_rate", label: "Change Failure Rate", unit: "%" },
  { value: "team_satisfaction", label: "Team Satisfaction", unit: "/5" },
  { value: "system_uptime", label: "System Uptime", unit: "%" },
];

interface MetricModalProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
}

export default function MetricModal({ open, onOpenChange }: MetricModalProps) {
  const [isSubmitting, setIsSubmitting] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const form = useForm<InsertKpiMetric>({
    resolver: zodResolver(insertKpiMetricSchema),
    defaultValues: {
      metricType: "",
      value: "",
      target: "",
      unit: "",
      notes: "",
    },
  });

  const createMetricMutation = useMutation({
    mutationFn: async (metricData: InsertKpiMetric) => {
      await apiRequest("POST", "/api/kpi-metrics", metricData);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/kpi-metrics"] });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboard/stats"] });
      toast({
        title: "Success",
        description: "Metric recorded successfully",
      });
      onOpenChange(false);
      form.reset();
    },
    onError: (error) => {
      toast({
        title: "Error", 
        description: "Failed to record metric",
        variant: "destructive",
      });
    },
    onSettled: () => {
      setIsSubmitting(false);
    },
  });

  const onSubmit = (data: InsertKpiMetric) => {
    setIsSubmitting(true);
    createMetricMutation.mutate(data);
  };

  const handleMetricTypeChange = (metricType: string) => {
    const selectedMetric = METRIC_TYPES.find(m => m.value === metricType);
    if (selectedMetric) {
      form.setValue("metricType", metricType);
      form.setValue("unit", selectedMetric.unit);
    }
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-lg" data-testid="metric-modal">
        <DialogHeader>
          <DialogTitle className="text-lg font-semibold text-slate-800">
            Record KPI Metric
          </DialogTitle>
        </DialogHeader>

        <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
          <div>
            <Label htmlFor="metricType" className="block text-sm font-medium text-slate-700 mb-2">
              Metric Type *
            </Label>
            <Select onValueChange={handleMetricTypeChange}>
              <SelectTrigger data-testid="select-metric-type">
                <SelectValue placeholder="Select metric type" />
              </SelectTrigger>
              <SelectContent>
                {METRIC_TYPES.map(metric => (
                  <SelectItem key={metric.value} value={metric.value}>
                    {metric.label}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
            {form.formState.errors.metricType && (
              <p className="text-sm text-red-600 mt-1">{form.formState.errors.metricType.message}</p>
            )}
          </div>

          <div className="grid grid-cols-2 gap-4">
            <div>
              <Label htmlFor="value" className="block text-sm font-medium text-slate-700 mb-2">
                Current Value *
              </Label>
              <Input
                id="value"
                {...form.register("value")}
                placeholder="0"
                className="w-full"
                data-testid="input-metric-value"
              />
              {form.formState.errors.value && (
                <p className="text-sm text-red-600 mt-1">{form.formState.errors.value.message}</p>
              )}
            </div>

            <div>
              <Label htmlFor="target" className="block text-sm font-medium text-slate-700 mb-2">
                Target Value
              </Label>
              <Input
                id="target"
                {...form.register("target")}
                placeholder="Target"
                className="w-full"
                data-testid="input-metric-target"
              />
            </div>
          </div>

          <div>
            <Label htmlFor="unit" className="block text-sm font-medium text-slate-700 mb-2">
              Unit
            </Label>
            <Input
              id="unit"
              {...form.register("unit")}
              placeholder="e.g. %, hours, per week"
              className="w-full"
              data-testid="input-metric-unit"
              readOnly
            />
          </div>

          <div>
            <Label htmlFor="notes" className="block text-sm font-medium text-slate-700 mb-2">
              Notes
            </Label>
            <Textarea
              id="notes"
              {...form.register("notes")}
              placeholder="Additional context or observations"
              rows={3}
              className="w-full"
              data-testid="textarea-metric-notes"
            />
          </div>

          <div className="flex justify-end space-x-3 pt-4">
            <Button 
              type="button" 
              variant="outline"
              onClick={() => onOpenChange(false)}
              data-testid="button-cancel-metric"
            >
              Cancel
            </Button>
            <Button 
              type="submit" 
              disabled={isSubmitting}
              className="bg-primary hover:bg-blue-800"
              data-testid="button-save-metric"
            >
              {isSubmitting ? "Recording..." : "Record Metric"}
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/forms/risk-modal.t.content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Label } from "@/components/ui/label";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertRiskSchema, type InsertRisk, type Risk } from "@shared/schema";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

const RISK_CATEGORIES = [
  "Technical Skills Atrophy",
  "Team Resistance to Change", 
  "Overwhelming Workload",
  "Stakeholder Misalignment",
  "Communication Breakdown",
  "Resource Constraints",
  "Timeline Pressure",
  "Knowledge Gap",
  "Process Failure",
  "Technology Risk",
];

interface RiskModalProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  risk?: Risk | null;
}

export default function RiskModal({ open, onOpenChange, risk }: RiskModalProps) {
  const [isSubmitting, setIsSubmitting] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const form = useForm<InsertRisk>({
    resolver: zodResolver(insertRiskSchema),
    defaultValues: {
      title: risk?.title || "",
      description: risk?.description || "",
      category: risk?.category || "",
      probability: risk?.probability || "medium",
      impact: risk?.impact || "medium",
      status: risk?.status || "active",
      mitigationPlan: risk?.mitigationPlan || "",
      contingencyPlan: risk?.contingencyPlan || "",
    },
  });

  const createRiskMutation = useMutation({
    mutationFn: async (riskData: InsertRisk) => {
      if (risk) {
        await apiRequest("PUT", `/api/risks/${risk.id}`, riskData);
      } else {
        await apiRequest("POST", "/api/risks", riskData);
      }
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/risks"] });
      toast({
        title: "Success",
        description: `Risk ${risk ? 'updated' : 'created'} successfully`,
      });
      onOpenChange(false);
      form.reset();
    },
    onError: (error) => {
      toast({
        title: "Error", 
        description: `Failed to ${risk ? 'update' : 'create'} risk`,
        variant: "destructive",
      });
    },
    onSettled: () => {
      setIsSubmitting(false);
    },
  });

  const onSubmit = (data: InsertRisk) => {
    setIsSubmitting(true);
    createRiskMutation.mutate(data);
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-3xl max-h-[90vh] overflow-y-auto" data-testid="risk-modal">
        <DialogHeader>
          <DialogTitle className="text-lg font-semibold text-slate-800">
            {risk ? "Edit Risk" : "Add New Risk"}
          </DialogTitle>
        </DialogHeader>

        <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
          <div>
            <Label htmlFor="title" className="block text-sm font-medium text-slate-700 mb-2">
              Risk Title *
            </Label>
            <Input
              id="title"
              {...form.register("title")}
              placeholder="e.g. Technical skills becoming outdated"
              className="w-full"
              data-testid="input-risk-title"
            />
            {form.formState.errors.title && (
              <p className="text-sm text-red-600 mt-1">{form.formState.errors.title.message}</p>
            )}
          </div>

          <div>
            <Label htmlFor="description" className="block text-sm font-medium text-slate-700 mb-2">
              Description
            </Label>
            <Textarea
              id="description"
              {...form.register("description")}
              placeholder="Detailed description of the risk and its potential consequences"
              rows={3}
              className="w-full"
              data-testid="textarea-risk-description"
            />
          </div>

          <div className="grid grid-cols-2 gap-4">
            <div>
              <Label htmlFor="category" className="block text-sm font-medium text-slate-700 mb-2">
                Category
              </Label>
              <Select onValueChange={(value) => form.setValue("category", value)} defaultValue={form.getValues("category") || undefined}>
                <SelectTrigger data-testid="select-risk-category">
                  <SelectValue placeholder="Select category" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="none">No Category</SelectItem>
                  {RISK_CATEGORIES.map((category) => (
                    <SelectItem key={category} value={category}>
                      {category}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div>
              <Label htmlFor="status" className="block text-sm font-medium text-slate-700 mb-2">
                Status
              </Label>
              <Select onValueChange={(value) => form.setValue("status", value)} defaultValue={form.getValues("status")}>
                <SelectTrigger data-testid="select-risk-status">
                  <SelectValue placeholder="Select status" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="active">Active</SelectItem>
                  <SelectItem value="mitigated">Mitigated</SelectItem>
                  <SelectItem value="resolved">Resolved</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>

          <div className="grid grid-cols-2 gap-4">
            <div>
              <Label htmlFor="probability" className="block text-sm font-medium text-slate-700 mb-2">
                Probability
              </Label>
              <Select onValueChange={(value) => form.setValue("probability", value)} defaultValue={form.getValues("probability") || undefined}>
                <SelectTrigger data-testid="select-risk-probability">
                  <SelectValue placeholder="Select probability" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="low">Low</SelectItem>
                  <SelectItem value="medium">Medium</SelectItem>
                  <SelectItem value="high">High</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <div>
              <Label htmlFor="impact" className="block text-sm font-medium text-slate-700 mb-2">
                Impact
              </Label>
              <Select onValueChange={(value) => form.setValue("impact", value)} defaultValue={form.getValues("impact") || undefined}>
                <SelectTrigger data-testid="select-risk-impact">
                  <SelectValue placeholder="Select impact" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="low">Low</SelectItem>
                  <SelectItem value="medium">Medium</SelectItem>
                  <SelectItem value="high">High</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>

          <div>
            <Label htmlFor="mitigationPlan" className="block text-sm font-medium text-slate-700 mb-2">
              Mitigation Plan
            </Label>
            <Textarea
              id="mitigationPlan"
              {...form.register("mitigationPlan")}
              placeholder="Steps to reduce the probability or impact of this risk"
              rows={3}
              className="w-full"
              data-testid="textarea-risk-mitigation"
            />
          </div>

          <div>
            <Label htmlFor="contingencyPlan" className="block text-sm font-medium text-slate-700 mb-2">
              Contingency Plan
            </Label>
            <Textarea
              id="contingencyPlan"
              {...form.register("contingencyPlan")}
              placeholder="Backup plan if the risk occurs despite mitigation efforts"
              rows={3}
              className="w-full"
              data-testid="textarea-risk-contingency"
            />
          </div>

          <div className="flex justify-end space-x-3 pt-4">
            <Button 
              type="button" 
              variant="outline"
              onClick={() => onOpenChange(false)}
              data-testid="button-cancel-risk"
            >
              Cancel
            </Button>
            <Button 
              type="submit" 
              disabled={isSubmitting}
              className="bg-primary hover:bg-blue-800"
              data-testid="button-save-risk"
            >
              {isSubmitting ? "Saving..." : risk ? "Update Risk" : "Add Risk"}
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/forms/task-modal.t.content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Label } from "@/components/ui/label";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertTaskSchema, type InsertTask } from "@shared/schema";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { PHASE_DATA } from "@/types";

interface TaskModalProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  task?: any; // For editing existing tasks
}

export default function TaskModal({ open, onOpenChange, task }: TaskModalProps) {
  const [isSubmitting, setIsSubmitting] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const form = useForm<InsertTask>({
    resolver: zodResolver(insertTaskSchema),
    defaultValues: {
      title: task?.title || "",
      description: task?.description || "",
      priority: task?.priority || "medium",
      status: task?.status || "pending",
      phaseId: task?.phaseId || "",
      dueDate: task?.dueDate ? new Date(task.dueDate).toISOString().split('T')[0] : undefined,
      notes: task?.notes || "",
    },
  });

  const createTaskMutation = useMutation({
    mutationFn: async (taskData: InsertTask) => {
      await apiRequest("POST", "/api/tasks", taskData);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/tasks"] });
      queryClient.invalidateQueries({ queryKey: ["/api/tasks/upcoming"] });
      queryClient.invalidateQueries({ queryKey: ["/api/dashboard/stats"] });
      toast({
        title: "Success",
        description: "Task created successfully",
      });
      onOpenChange(false);
      form.reset();
    },
    onError: () => {
      toast({
        title: "Error", 
        description: "Failed to create task",
        variant: "destructive",
      });
    },
    onSettled: () => {
      setIsSubmitting(false);
    },
  });

  const onSubmit = (data: InsertTask) => {
    setIsSubmitting(true);
    createTaskMutation.mutate(data);
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto" data-testid="task-modal">
        <DialogHeader>
          <DialogTitle className="text-lg font-semibold text-slate-800">
            {task ? "Edit Task" : "Create New Task"}
          </DialogTitle>
        </DialogHeader>

        <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
          <div>
            <Label htmlFor="title" className="block text-sm font-medium text-slate-700 mb-2">
              Task Title
            </Label>
            <Input
              id="title"
              {...form.register("title")}
              placeholder="Enter task title"
              className="w-full"
              data-testid="input-task-title"
            />
            {form.formState.errors.title && (
              <p className="text-sm text-red-600 mt-1">{form.formState.errors.title.message}</p>
            )}
          </div>

          <div>
            <Label htmlFor="description" className="block text-sm font-medium text-slate-700 mb-2">
              Description
            </Label>
            <Textarea
              id="description"
              {...form.register("description")}
              placeholder="Task description"
              rows={3}
              className="w-full"
              data-testid="textarea-task-description"
            />
          </div>

          <div className="grid grid-cols-2 gap-4">
            <div>
              <Label htmlFor="phaseId" className="block text-sm font-medium text-slate-700 mb-2">
                Phase
              </Label>
              <Select onValueChange={(value) => form.setValue("phaseId", value)} defaultValue={form.getValues("phaseId") || undefined}>
                <SelectTrigger data-testid="select-task-phase">
                  <SelectValue placeholder="Select phase" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="none">No Phase</SelectItem>
                  {PHASE_DATA.map((phase) => (
                    <SelectItem key={phase.id} value={phase.id}>
                      {phase.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div>
              <Label htmlFor="priority" className="block text-sm font-medium text-slate-700 mb-2">
                Priority
              </Label>
              <Select onValueChange={(value) => form.setValue("priority", value as "high" | "medium" | "low")} defaultValue={form.getValues("priority")}>
                <SelectTrigger data-testid="select-task-priority">
                  <SelectValue placeholder="Select priority" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="high">High</SelectItem>
                  <SelectItem value="medium">Medium</SelectItem>
                  <SelectItem value="low">Low</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>

          <div>
